(()=>{let i=document.currentScript.getAttribute("data-id"),r="http://localhost:3000/api/trackClick";if(i){let n=0;document.addEventListener("click",e=>{var t=Date.now();t-n<500||(n=t,t=JSON.stringify({projectId:i,url:window.location.href,relativeX:e.clientX/window.innerWidth,relativeY:e.clientY/window.innerHeight,screenWidth:window.innerWidth,screenHeight:window.innerHeight,timestamp:(new Date).toISOString(),userAgent:navigator.userAgent,element:e.target.tagName,class:e.target.className,id:e.target.id}),navigator.sendBeacon?navigator.sendBeacon(r,t):fetch(r,{method:"POST",headers:{"Content-Type":"application/json"},body:t}).catch(e=>{console.error("Heatpeek: Error sending click data:",e)}))})}else console.error("Heatpeek: Project ID is required")})();